package main

import (
	"net/http"

	"github.com/gin-gonic/gin"
)

func main() {
	// Gin router
	router := gin.Default()
	router.Use(func(c *gin.Context) {
		c.Writer.Header().Set("Access-Control-Allow-Origin", "*")
		c.Writer.Header().Set("Access-Control-Allow-Methods", "GET,POST,PUT,DELETE,OPTIONS")
		c.Writer.Header().Set("Access-Control-Allow-Headers", "Content-Type")

		if c.Request.Method == "OPTIONS" {

			c.AbortWithStatus(http.StatusNoContent)
			return
		}
	})

	type Item struct {
		ID       int    `json:"id"`
		Name     string `json:"name"`
		Price    int    `json:"price"`
		Quantity int    `json:"quantity"`
		Image    string `json:"image"`
	}

	// Add some initial items to the vending machine
	items := []Item{
		{ID: 1, Name: "Mirinda", Price: 50, Quantity: 5, Image: "data:image/jpeg;base64,"},
		{ID: 2, Name: "Darkfantasy", Price: 100, Quantity: 5, Image: "data:image/jpeg;base64,"},
		{ID: 3, Name: "Lays", Price: 20, Quantity: 5, Image: "data:image/jpeg;base64,"},
		{ID: 4, Name: "Littleheart", Price: 30, Quantity: 5, Image: "data:image/jpeg;base64,"},
	}

	// Get all items from the vending machine
	router.GET("/items", func(c *gin.Context) {
		c.JSON(http.StatusOK, items)

	})

	// Start the server
	router.Run(":8081")
}
